changeSet.sql:
CREATE OR REPLACE FUNCTION safe_convert_utf8(data bytea) RETURNS text AS $$
BEGIN
    BEGIN
        RETURN convert_from(data, 'UTF8');
    EXCEPTION WHEN OTHERS THEN
        RETURN encode(data, 'escape');
    END;
END;
$$ LANGUAGE plpgsql IMMUTABLE;

ALTER TABLE mail_processed_messages
ALTER COLUMN subject TYPE TEXT
USING safe_convert_utf8(subject);

DROP FUNCTION safe_convert_utf8(bytea);
